<?php class App_Models_Submissions extends Pf_Core_Model{	public function getByRegionAndQuality($country, $region, $quality)	{		$sql = 'SELECT * FROM Submissions 			WHERE country=:country 			AND region=:region 			AND quality=:quality		';		$stmt = $this->db->prepare($sql);        $stmt->bindValue(':country', $country);        $stmt->bindValue(':region', $region);        $stmt->bindValue(':quality', $quality);		$stmt->execute();			$rows = $stmt->fetchAll(PDO::FETCH_ASSOC);				return $rows;	}	public function getSizeByRegion($country, $region) 	{		$sql = "SELECT COUNT(*) FROM  Submissions WHERE country=:country and region=:region";	        $stmt = $this->db->prepare($sql);        $stmt->bindValue(':country', $country);        $stmt->bindValue(':region', $region);        $stmt->execute();        return $stmt->fetchColumn();	}	public function getByRegion($country, $region, $max=10, $offset=0)	{		$sql = 'SELECT * FROM Submissions WHERE country=:country AND region=:region ORDER BY id DESC LIMIT '.$offset.','.$max;			$stmt = $this->db->prepare($sql);        $stmt->bindValue(':country', $country);        $stmt->bindValue(':region', $region);		$stmt->execute();		$rows = $stmt->fetchAll(PDO::FETCH_ASSOC);		return $rows;	}}?>